/* global describe, it, expect, jsPDF */

describe("Module: Standard Font Metrics", () => {
  beforeAll(loadGlobals);
  it("uncompress", () => {
    expect(
      jsPDF.API.__fontmetrics__.uncompress(
        "{'widths'{k3w'fof'6o}'kerning'{'fof'-6o}}"
      )
    ).toEqual({ widths: { 0: 60, fof: 100 }, kerning: { fof: -100 } });
  });
  it("uncompress fail", () => {
    expect(function() {
      jsPDF.API.__fontmetrics__.uncompress({
        widths: { 0: 60, fof: 100 },
        kerning: { fof: -100 }
      });
    }).toThrow(new Error("Invalid argument passed to uncompress."));
    expect(function() {
      jsPDF.API.__fontmetrics__.compress({
        widths: "invalid",
        kerning: { fof: -100 }
      });
    }).toThrow(new Error("Don't know what to do with value type string."));
  });
  it("compress", () => {
    expect(
      jsPDF.API.__fontmetrics__.compress({
        widths: { 0: 60, fof: 100 },
        kerning: { fof: -100 }
      })
    ).toEqual("{'widths'{k3w'fof'6o}'kerning'{'fof'-6o}}");
  });
  it("compress and uncompress", () => {
    var value = { widths: { 0: 60, fof: 100 }, kerning: { fof: -100 } };
    expect(
      jsPDF.API.__fontmetrics__.uncompress(
        jsPDF.API.__fontmetrics__.compress(value)
      )
    ).toEqual(value);
  });
  it("uncompress and compress", () => {
    var value =
      "{19m8n201n9q201o9r201s9l201t9m201u8m201w9n201x9o201y8o202k8q202l8r202m9p202q8p20aw8k203k8t203t8v203u9v2cq8s212m9t15m8w15n9w2dw9s16k8u16l9u17s9z17x8y17y9y}";
    expect(
      jsPDF.API.__fontmetrics__.uncompress(
        jsPDF.API.__fontmetrics__.compress(
          jsPDF.API.__fontmetrics__.uncompress(value)
        )
      )
    ).toEqual(jsPDF.API.__fontmetrics__.uncompress(value));
    value =
      "{'widths'{'fof'}'kerning'{cl{4qs5ku17sw5ou5qy5rw201ss5tw201ws}201s{201ss}201t{ckw4lwcmwcnwcowcpwclw4wu201ts}2k{201ts}2w{4qs5kw5os5qx5ru17sx5tx}2x{17sw5tw5ou5qu}2y{4qs5kw5os5qx5ru17sx5tx}'fof'-6o7t{ckuclucmucnucoucpu4lu5os5rs}3u{17su5tu5qs}3v{17su5tu5qs}7p{17sw5tw5qs}ck{4qs5ku17sw5ou5qy5rw201ss5tw201ws}4l{4qs5ku17sw5ou5qy5rw201ss5tw201ws}cm{4qs5ku17sw5ou5qy5rw201ss5tw201ws}cn{4qs5ku17sw5ou5qy5rw201ss5tw201ws}co{4qs5ku17sw5ou5qy5rw201ss5tw201ws}cp{4qs5ku17sw5ou5qy5rw201ss5tw201ws}6l{17su5tu5os5qw5rs}17s{2ktclvcmvcnvcovcpv4lv4wuckv}5o{ckwclwcmwcnwcowcpw4lw4wu}5q{ckyclycmycnycoycpy4ly4wu5ms}5r{cktcltcmtcntcotcpt4lt4ws}5t{2ktclvcmvcnvcovcpv4lv4wuckv}7q{cksclscmscnscoscps4ls}6p{17su5tu5qw5rs}ek{5qs5rs}el{17su5tu5os5qw5rs}em{17su5tu5os5qs5rs}en{17su5qs5rs}eo{5qs5rs}ep{17su5tu5os5qw5rs}es{5qs}et{17su5tu5qw5rs}eu{17su5tu5qs5rs}ev{5qs}6z{17sv5tv5os5qx5rs}fm{5os5qt5rs}fn{17sv5tv5os5qx5rs}fo{17sv5tv5os5qx5rs}fp{5os5qt5rs}fq{5os5qt5rs}7r{ckuclucmucnucoucpu4lu5os}fs{17sv5tv5os5qx5rs}ft{17ss5ts5qs}fu{17sw5tw5qs}fv{17sw5tw5qs}fw{17ss5ts5qs}fz{ckuclucmucnucoucpu4lu5os5rs}}}";
    expect(
      jsPDF.API.__fontmetrics__.uncompress(
        jsPDF.API.__fontmetrics__.compress(
          jsPDF.API.__fontmetrics__.uncompress(value)
        )
      )
    ).toEqual(jsPDF.API.__fontmetrics__.uncompress(value));
  });
});
